    }
    if (((abs(float(cc_debug_view_mode.x) - float(CC_SURFACES_DEBUG_VIEW_TRANSMIT_DIRECT_SPECULAR)) < EPSILON_LOWP) && (cc_surfaceTransform.y != 3.0)))
    {
        color.rgb = directTransmitSpecular;
        isSRGBColor = true;
    }
    if (((abs(float(cc_debug_view_mode.x) - float(CC_SURFACES_DEBUG_VIEW_TRANSMIT_ENV_SPECULAR)) < EPSILON_LOWP) && (cc_surfaceTransform.y != 3.0)))
    {
        color.rgb = environmentTransmitSpecular;
        isSRGBColor = true;
    }
    if (((abs(float(cc_debug_view_mode.x) - float(CC_SURFACES_DEBUG_VIEW_TRANSMIT_DIRECT_DIFFUSE)) < EPSILON_LOWP) && (cc_surfaceTransform.y != 3.0)))
    {
        color.rgb = directTransmitDiffuse;
        isSRGBColor = true;
    }
    if (((abs(float(cc_debug_view_mode.x) - float(CC_SURFACES_DEBUG_VIEW_TRANSMIT_ENV_DIFFUSE)) < EPSILON_LOWP) && (cc_surfaceTransform.y != 3.0)))
    {
        color.rgb = environmentTransmitDiffuse;
        isSRGBColor = true;
    }
    if (((abs(float(cc_debug_view_mode.x) - float(CC_SURFACES_DEBUG_VIEW_TRANSMIT_ALL)) < EPSILON_LOWP) && (cc_surfaceTransform.y != 3.0)))
    {
        color.rgb = directTransmitSpecular + environmentTransmitSpecular + directTransmitDiffuse + environmentTransmitDiffuse;
        isSRGBColor = true;
    }
    if (((abs(float(cc_debug_view_mode.x) - float(CC_SURFACES_DEBUG_VIEW_DIRECT_2ND_SPECULAR)) < EPSILON_LOWP) && (cc_surfaceTransform.y != 3.0)))
    {
        color.rgb = direct2ndSpecular * specularColorWithLighting2ndSpecular;
        isSRGBColor = true;
    }
    if (((abs(float(cc_debug_view_mode.x) - float(CC_SURFACES_DEBUG_VIEW_ENVIRONMENT_2ND_SPECULAR)) < EPSILON_LOWP) && (cc_surfaceTransform.y != 3.0)))
    {
        color.rgb = environment2ndSpecular * specularColorWithLighting2ndSpecular;
        isSRGBColor = true;
    }
    if (((abs(float(cc_debug_view_mode.x) - float(CC_SURFACES_DEBUG_VIEW_2ND_SPECULAR_ALL)) < EPSILON_LOWP) && (cc_surfaceTransform.y != 3.0)))
    {
        color.rgb = (direct2ndSpecular + environment2ndSpecular) * specularColorWithLighting2ndSpecular;
        isSRGBColor = true;
    }
    return isSRGBColor;
  }
